const burgerToggler=document.querySelector(".burger"),navLinksContainer=document.querySelector(".navlinks-container"),btn=document.querySelector("div#backToTop"),toggleNav=e=>{burgerToggler.classList.toggle("open");"true"===burgerToggler.getAttribute("aria-expanded")?burgerToggler.setAttribute("aria-expanded","true"):burgerToggler.setAttribute("aria-expanded","false"),navLinksContainer.classList.toggle("open")};function backToTop(){window.scroll({top:0,left:0,behavior:"smooth"})}function scrollTop(){window.scrollY;window.scrollY>20?btn.classList.add("visible"):btn.classList.remove("visible")}document.addEventListener("DOMContentLoaded",(function(e){burgerToggler.addEventListener("click",toggleNav),btn.addEventListener("click",backToTop),window.onscroll=function(){scrollTop()}}));